import { useState } from "react";
import CurrentTurn from "./CurrentTurn";

function Tile () {

    const [filled, setFilled] = useState(false);

    const [currentValue, setCurrentValue] = useState('')

    const playRound = () => {

        setCurrentValue(prevCurrentValue => prevCurrentValue + 'X')
        setFilled(prevFilled => prevFilled = true)
        CurrentTurn.turn = 2;

    }

    const checkFilled = () => {return (filled ? '' : playRound())}

    return(
        <div className="tile" onClick={checkFilled}>
            {currentValue}
        </div>
    )
}

export default Tile
