function Gameboard () {

    const [currentTurn, setCurrentTurn] = useState(0);

    const [data, setData] = useState(['', '', '', '', '', '', '', '', '']);

    function draw (event, index) {

        if(data[index - 1] === '') {

            let currentValue = currentTurn ? 'X' : 'O';
            setData(data[index - 1] = currentValue);
            event.target.innerText = currentValue;
            setCurrentTurn(currentTurn === 0 ? 0 : 1)

        }

    }

    return(
        <div className="board">
            <div className="tile" onClick={draw(e, 1)}></div>
        </div>
    )
}

export default Gameboard
